$('#searchS').on('submit', e => {
    e.preventDefault()
    const formData = $('#searchS').serializeArray()
    var o = {}
    $.each(formData, function () {
      if (o[this.name]) {
        if (!o[this.name].push) {
          o[this.name] = [o[this.name]];
        }
        o[this.name].push(this.value || '');
      } else {
        o[this.name] = this.value || '';
      }
    })
    console.log(o)
    if(o.type=="cinameName"){
        var str = {
            "name":o.text
        }
        sessionStorage.setItem('SearchCInformation', JSON.stringify(str));
        window.open("searchCinema.html");
    }
    else if(o.type=="movieName"){
        var str = {
            "name":o.text
        }
        sessionStorage.setItem('SearchCInformation', JSON.stringify(str));
        window.open("searchMovie.html");
    }
  
    
  })
