var index = 0;
        //左右箭头，手动轮播
        var box = document.querySelector(".box");
        var next = document.querySelector("#right");
        var prev = document.querySelector("#left");
        next.onclick = function () {
            nextpic();
        }
        prev.onclick = function () {
            prevpic();
        }
        function nextpic() {
            var newLeft;
            if (box.style.left === "-7200px") {
                newLeft = -2400;
            } else {
                newLeft = parseInt(box.style.left) - 1200;
            }
            box.style.left = newLeft + "px";
            index++;
            if (index > 4) {
                index = 0;
            }
            showCurrentDot();
        }
        function prevpic() {
            var newLeft;
            if (box.style.left === "0px") {
                newLeft = -4800;
            } else {
                newLeft = parseInt(box.style.left) + 1200;
            }
            box.style.left = newLeft + "px";
            index--;
            if (index < 0) {
                index = 4;
            }
            showCurrentDot();
        }
        //自动播放
        var timer = null;
        function autoPlay() {
            timer = setInterval(function () {
                nextpic();
            }, 2000);
        }
        autoPlay();
        //鼠标放上停止轮播
        var container = document.querySelector(".container");
        container.onmouseenter = function () {
            clearInterval(timer);
        }
        container.onmouseleave = function () {
            autoPlay();
        }
