        //中间的内容
        var data = document.getElementById("data");
        var three = document.getElementById("three");

        var dataShow = document.getElementById("big-data");
        var threeD = document.getElementById("threeD");

        three.addEventListener('click', function () {
            threeD.style = "display:block";
            dataShow.style = "display:none";
        })

        data.addEventListener('click', function () {
            dataShow.style = "display:block";
            threeD.style = "display:none";
        })

        var arrow = document.getElementById("arrow");
        var function1 = document.getElementsByClassName('function')[0];
        var function2 = document.getElementsByClassName('function-hidden')[0];


        var flag = 1;
        arrow.addEventListener('click', function () {
            if (flag % 2 != 0) {
                function2.style = "display:block;animation: fun-show 1.5s; ";
                function1.style = "display:none;animation: fun-die 1.5s;";
                flag++;

            }
            else {
                function1.style = "display:block;animation: fun-show 1.5s ;";
                function2.style = "display:none;animation: fun-die 1.5s;";
                flag++;
            }

        })