var userJsonStr = sessionStorage.getItem('cinema');
userEntity = JSON.parse(userJsonStr);




$('body').on("click", '.delete_movie', function () {
  // 删除按钮所在的一行
  var parent = $(this).parent()

  let line = parent.eq(0)[0]
  $.ajax({
    url: `http://localhost:8080/AdminDelMovie/${line.id}/${userEntity.cinemaId}`,
    type: 'delete',
    dataType: 'json',
    catch: false,      //清除上次请求的缓存
    contentType: 'application/json;charset=UTF-8',
    success: data => {
      console.log(data)

      console.log('删除成功')
      parent.remove()
    }
  })
})